DROP TABLE IF EXISTS `user_movies_behaviours`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `user_movies_behaviours` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `MovieName` varchar(255) NOT NULL,
  `MovieIMDB` varchar(255) NOT NULL,
  `WatchStatus` varchar(255) DEFAULT NULL,
  `Review` varchar(1023) DEFAULT NULL,
  `Rating` varchar(255) DEFAULT NULL,
  `UserUrl` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`ID`),
  KEY `UserUrl` (`UserUrl`),
  KEY `MovieIMDB` (`MovieIMDB`),
  CONSTRAINT `user_movies_behaviours_ibfk_1` FOREIGN KEY (`UserUrl`) REFERENCES `users` (`url`),
  CONSTRAINT `user_movies_behaviours_ibfk_2` FOREIGN KEY (`MovieIMDB`) REFERENCES `movies_info` (`IMDB`)
) ENGINE=InnoDB AUTO_INCREMENT=429 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=DYNAMIC COMMENT='保存用户在电影上的行为信息';
/*!40101 SET character_set_client = @saved_cs_client */;

